import {useState} from 'react'
import './Contact.css';

function Contact() {

    const onSubmitHandler = (event) => {
        event.preventDefault();
        // console.log(event.target)
        // console.log(`${event.target.fname.value} ${event.target.lname.value}`);
        // console.log("Submit");
    };

    const [chekName, setCheckName] = useState({
        toutched: false,
        isValid: false,
        errMase : ''
    });

    const checkFirstAndLastName = (event) => {
        let valFN = event.target.value.trim();
        let valid = {...chekName};
        valid.toutched = true;

        if(valFN.length <= 0){
            valid.isValid = false;
            valid.errMase = "entart the name";
        }
        else {
            valid.isValid = true;
            valid.errMase = "";
        }

        setCheckName({...valid});
        console.log(valid)
    }
    

    

    return (
        <div className='contact'>
            <div className='container'>
            <h3 className='special-heading'>Contact</h3>
                <p>We are born to create</p>
                <form onSubmit={onSubmitHandler}>
                    <input type='text'
                     name="fname"
                     onBlur={checkFirstAndLastName}
                     placeholder="Enter The First Name.."
                     className={chekName.toutched && chekName.isValid? '':'border-red'}/>
                    {chekName.errMase ? (
                        <small className="text-red">{chekName.errMase}</small>
                    ): ("")}

                    <input type='text' 
                    name="lname" 
                    onBlur={checkFirstAndLastName} 
                    placeholder="Enter The Last Name.."
                    lassName={chekName.toutched && chekName.isValid? '':'border-red'}/>
                    {chekName.errMase ? (
                        <small className="text-red">{chekName.errMase}</small>
                    ): ("")}
                    <input type='email' name="email" placeholder="Enter Your Email.."/>
                    <textarea type="text" name="mesage"  placeholder ="Enter Your Masage" />
                    <button type='submit' name="submit">Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Contact
