import {useState} from 'react'
import './Contact.css';

function Contact() {

    const onSubmitHandler = (event) => {
        event.preventDefault();
        // console.log(event.target)
        // console.log(`${event.target.fname.value} ${event.target.lname.value}`);
        // console.log("Submit");
    };

    const [chekName, setCheckName] = useState({
        toutched: false,
        isValid: false,
        errMase : ''
    });

    const checkFirstAndLastName = (event) => {
        let valFN = event.target.value.trim();
        let valid = {...chekName};
        valid.toutched = true;

        if(valFN.length <= 0){
            valid.isValid = false;
            valid.errMase = "entart the name";
        }
        else {
            valid.isValid = true;
        }

        setCheckName({...valid});
        console.log(valid)
    }
    

    const checkMasseg = (event) => {
        let valFN = event.target.value.trim();
        let valid = {...chekName};
        valid.toutched = true;

        if(valFN.length <= 0){
            valid.isValid = false;
            valid.errMase = "entart the Massage";
        } else if (valFN.split(" ").length < 3){
            valid.isValid = false;
            valid.errMase = "Massage Must be more than 3 words";
        }else if (valFN.split(" ").length > 100){
            valid.isValid = false;
            valid.errMase = "Massage Must be less than 10 words";
        }else {
            valid.isValid = true;
        }
        setCheckName({...valid})
        console.log(valid)
    };

    return (
        <div className='contact'>
            <div className='container'>
            <h3 className='special-heading'>Contact</h3>
                <p>We are born to create</p>
                <form onSubmit={onSubmitHandler}>
                    <input type='text' name="fname" onBlur={checkFirstAndLastName} placeholder="Enter The First Name.."/>
                    <input type='text' name="lname" placeholder="Enter The Last Name.."/>
                    <input type='email' name="email" placeholder="Enter Your Email.."/>
                    <textarea type="text" name="mesage" placeholder ="Enter Your Masage"/>
                    <button type='submit' name="submit">Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Contact
